import React from "react";
import ReactDom from "react-dom";
import "./index.css";

const fName = "Vishal";
const lName = "lohemor";
const toDayDate = new Date().toLocaleDateString();
const currTime = new Date().toLocaleTimeString();
const img1 = "https://picsum.photos/200/300";
const img2 = "https://picsum.photos/250/300";
const img3 = "https://picsum.photos/300/300";
const uidaiLink = "https://uidai.gov.in/";

const heading = {
  color: "#c5555591",
  textTransform: "capitalize",
  textAlign: "center",
  fontWeight: "bold",
  textShadow: "0px 2px 4px #ffe9c5",
  margin: "70px 0",
  fontFamily: "Josefin Slab, serif",
};

let greetingFunc = () => {
  console.log("currTime", currTime);
  let date = new Date();
  let greetingText = "";
  if (date.getHours() < 12) {
    greetingText = "Good Morning !";
  } else if (date.getHours() > 12 && date.getHours() < 20) {
    greetingText = "Good Evening !";
  } else if (date.getHours() > 20) {
    greetingText = "Good Night !";
  }
  return greetingText;
};
let greetingTextColorFunc = () => {
  let date = new Date();
  let greetingTextColor = {};
  if (date.getHours() < 12) {
    greetingTextColor = {
      color: "green",
      margin: "0px 20px 20px",
    };
  } else if (date.getHours() > 12 && date.getHours() < 20) {
    greetingTextColor = {
      color: "blue",
      margin: "0px 20px 20px",
    };
  } else if (date.getHours() > 20) {
    greetingTextColor = {
      color: "red",
      margin: "0px 40px 20px",
    };
  }
  return greetingTextColor;
};

const greetingDiv = {
  display: "flex",
  backgroundColor: "pink",
  justifyContent: "center",
  width: "500px",
  borderRadius: "10%",
  boxShadow: "0px 5px ffe9c5",
  textShadow: "0px 2px 4px #ffe9c5",
};

ReactDom.render(
  <>
    <h1 style={heading}>{`My name is ${fName} ${lName}`}</h1>

    <h2 style={heading}>
      {`Today date is : ${toDayDate} , Current time is : ${currTime} `}
    </h2>
    <div style={greetingDiv}>
      <h1>Hello Sir,</h1>
      <h1 style={greetingTextColorFunc()}>{`${greetingFunc()}`} </h1>
    </div>

    <div className="img_div">
      <img src={img1} alt="randomImages" />
      <img src={img2} alt="randomImages" />
      <a href={uidaiLink} target="_blank" rel="noreferrer">
        <img src={img3} alt="randomImages" />
      </a>
    </div>
  </>,
  document.getElementById("root")
);
