import React from "react";

const toDayDate = new Date().toLocaleDateString();
const currTime = new Date().toLocaleTimeString();
const greetingDiv = {
  display: "flex",
  backgroundColor: "pink",
  justifyContent: "center",
  alignItems: "center",
  width: "100%",
  height: "50px",
  borderRadius: "5%",
  boxSizing: "border-box",
  boxShadow: "0px 5px ffe9c5",
  textShadow: "0px 2px 4px #ffe9c5",
  margin: "0px 20% 20px",
};
let greetingTextColorFunc = () => {
  let date = new Date();
  let greetingTextColor = {};
  if (date.getHours() < 12) {
    greetingTextColor = {
      color: "green",
      margin: "0px 20px 20px",
    };
  } else if (date.getHours() > 12 && date.getHours() < 20) {
    greetingTextColor = {
      color: "orange",
      margin: "0px 20px 20px",
    };
  } else if (date.getHours() > 20) {
    greetingTextColor = {
      color: "black",
      margin: "0px 20px 20px",
    };
  }
  return greetingTextColor;
};

let greetingFunc = () => {
  console.log("currTime", currTime);
  let date = new Date();
  let greetingText = "";
  if (date.getHours() < 12) {
    greetingText = "Good Morning !";
  } else if (date.getHours() > 12 && date.getHours() < 20) {
    greetingText = "Good Evening !";
  } else if (date.getHours() > 20) {
    greetingText = "Good Night !";
  }
  return greetingText;
};

function Greeting() {
  return (
    <>
      <div style={greetingDiv}>
        <h1>
          Hello Sir,
          <span style={greetingTextColorFunc()}>{`${greetingFunc()}`} </span>
        </h1>
      </div>
    </>
  );
}

export default Greeting;
